var Article=require("./Article");
var DatabaseDAO=require("../DatabaseDAO");

function ArticleDAO(){
	this.addArticle=function(article,fun){
		var sql="insert into article values(null,'"+article.getTitle()+"','"+article.getContent()+"')";
		var databaseDAO=new DatabaseDAO();
		databaseDAO.getConnection();
		databaseDAO.insert(sql,fun);
	}
	this.selectArticle=function(article,fun){
		var sql="select * from article where id="+article.getId();
		var databaseDAO=new DatabaseDAO();
		databaseDAO.getConnection();
		databaseDAO.select(sql,fun);
	}
}

module.exports=ArticleDAO;