function DatabaseDAO(){
	var connection=null;
	this.getConnection=function(){
		var mysql=require("mysql");
		connection=mysql.createConnection({
			host:"localhost",
			user:"root",
			password:"",
			database:"blog",
			port:3306
		});
		connection.connect();

	};
	this.delete=function(sql,fun){
		connection.query(sql,fun);
	};
	this.select=function(sql,fun){
		connection.query(sql,fun)
	};
	this.insert=function(sql,fun){
		connection.query(sql,fun);
	};
	this.update=function(sql,fun){
		connection.query(sql,fun);
	};
	this.close=function(){
		if(connection != null){
			connection.release();
			connection=null;
		}
	}
}





module.exports=DatabaseDAO;