function DatabaseDAO(){
	var connection=null;
	this.getConnection=function(){
		var mysql=require("mysql");
		connection=mysql.createConnection({
			host:"localhost",
			user:"root",
			password:"",
			database:"blog",
			port:3306
		});
		connection.connect();

	};
	this.delete=function(sql){
		connection.query(sql,function(err,res){
			if(err){
				throw err;
			}else{
				if(res.affectedRows == 0){
					return false;
				}else{
					return true;
				}
			}
		})
	};
	this.select=function(sql,fun){
		connection.query(sql,fun)
	};
	this.insert=function(sql){
		connection.query(sql,function(err,res){
			if(err){
				throw err;
			}else{
				if(res.affectedRows == 0){
					return false;
				}else{
					return true;
				}
			}
		})
	};
	this.update=function(sql){
		connection.query(sql,function(err,res){
			if(err){
				throw err;
			}else{
				if(res.affectedRows == 0){
					return false;
				}else{
					return true;
				}
			}
		})
	};
	this.close=function(){
		if(connection != null){
			connection.release();
			connection=null;
		}
	}
}





module.exports=DatabaseDAO;