function User(name,pass){
	this.username=name;
	this.password=pass;
	this.id="";
	this.getUsername=function(){
		return this.username;
	}
	this.setUsername=function(name){
		this.username=name;
	}
	this.getPassword=function(){
		return this.password;
	}
	this.setPassword=function(pass){
		this.password=pass;
	}
	this.getId=function(){
		return this.id;
	}
	this.setId=function(id){
		this.id=id;
	}
}

module.exports=User;